import React,{Component} from 'react'
import {operations} from '../covid19/operations/Operations'


class HomePage extends Component {
    state = {
      globalInfo:[]
    };
  
  
    async componentDidMount() {
      await operations
        .getSummary()
        .then(data => this.setState({ globalInfo: data.data.Global }));
      
      // await operations.getFlag('spain')
    }
  
  
    render() {
      
      console.log('GLOBALINFO', this.state.globalInfo.TotalConfirmed)
      return(
          <>
           
       <h2> COVID</h2>
          </>
          
       ) ;
      
    }
  }
  export default HomePage;
