import React, { Component } from "react";
import CovidList from "./CovidList/CovidList";
import {operations} from './operations/Operations'


class Covid19 extends Component {
  state = {
    summaryAray:null
  };


  async componentDidMount() {
    await operations
      .getSummary()
      .then(data => this.setState({ summaryAray: data.data }));
    
    // await operations.getFlag('spain')
  }


  render() {
    const {Countries}=this.state.summaryAray;
    return(
        <>
         
       <CovidList Countries={Countries}/>
        
        </>
        
     ) ;
    
  }
}
export default Covid19;
