import React from 'react'
import css from '../CovidItem/covidItem.module.css'
import LatestNews from "../LatestNews/LatestNews";

const CovidItem = ({maxConfirmed,maxDead,totalRecovered}) => {
    console.log('maxConfirmed', maxConfirmed)
   
    return (
      <>
      <ul className={css.maxConfirmed} > 
      {maxConfirmed.map(country=>(
          <li key={country.Country} className={css.countryLi} >
              <div className={css.liDiv}>
             <h2 className={css.country}>{country.Country}</h2>
      <p  className={css.info}>Total cases:{country.TotalConfirmed}</p>
      <p className={css.info}>New cases:{country.NewConfirmed}</p>
      <p className={css.info}>Total deaths:{country.TotalDeaths}</p>
      <p className={css.info}>New deaths:{country.NewDeaths}</p>
      <p className={css.info}>Total recovered:{country.TotalRecovered}</p>
      <p className={css.info}>New recovered:{country.NewRecovered}</p>
      </div>
      <div className={css.latestNewsDiv}>
      <LatestNews/>
      </div>
 </li>


      ))}
  </ul>
 
      </>
    );
  };
  export default CovidItem;
