import React, { Component } from "react";
import { operations } from "../covid19/operations/Operations";
import css from "../homePage/homepage.module.css";

class HomePage extends Component {
  state = {
    globalInfo: [],
  };

  async componentDidMount() {
    await operations
      .getSummary()
      .then((data) => this.setState({ globalInfo: data.data }));

    // await operations.getFlag('spain')
  }

  render() {
    console.log("GLOBALINFO", this.state.globalInfo);
    const {Global,Date}=this.state.globalInfo

    console.log('Global', this.state.globalInfo.Global);

    return (
      <>
        {this.state.globalInfo.Global&&
        <div className={css.homePageContainer}>
          <h1 className={css.covidTitle}> COVID 19 info</h1>
          <div className={css.totalCasesContainer}>
              <h2 className={css.totalCasesTitle}> Total cases in the world</h2>
    <p className={css.totConfirmed}>Total Confirmed:{Global.TotalConfirmed.toLocalString()}</p>
    <p className={css.totConfirmed}>New Confirmed cases :{Global.NewConfirmed}</p>
    <p className={css.totConfirmed}>Total Deaths :{Global.TotalDeaths}</p>
    <p className={css.totConfirmed}>Total Deaths :{Global.NewDeaths}</p>
    <p className={css.totConfirmed}>Total Recovered :{Global.TotalRecovered}</p>
    <p className={css.totConfirmed}>New Recovered :{Global.NewRecovered}</p>
    


          </div>
        </div>
  }
      </>
    );
  }
}
export default HomePage;
