import React, { Component } from "react";
import CovidItem from "../CovidItem/CovidItem";
import { operations } from "../operations/Operations";
class CovidList extends Component {
  state = {
    summaryAray: [],
    maxConfirmed: [],
    maxConfirmed: []
  };

  async componentDidMount() {
    await operations
      .getSummary()
      .then(data => this.setState({ summaryAray: data.data.Countries }));
    await this.TotalConfirmed();
  }

  TotalConfirmed = () => {
    const confirmed = this.state.summaryAray.sort(
      (b, a) => a.TotalConfirmed - b.TotalConfirmed
    
    );
    const maxConfirmed=confirmed.slice([0], [5]);
    console.log('object',maxConfirmed)
  };

  maxConfirmed = () => {
    const maxConfirmed = this.state.summaryAray.slice([0], [5]);
    console.log("maxConfirmed", maxConfirmed);
  };

  render() {
    const { summaryAray } = this.state;

    console.log("summaryAray", summaryAray);
    return (
      <>
        <CovidItem summaryAray={summaryAray} />
      </>
    );
  }
}
export default CovidList;
