import React, { useEffect, useState } from "react";
import SearchForm from "./SearchForm";
import { operations } from "../operations/Operations";
import LatestNews from "../LatestNews/LatestNews";
import { SearchFormResult } from "./SearchFormResult";

export const SearhPage = () => {
  const initialState = {
    searchQuery: '',
    searchinfo: '',
    flagQuery: '',
    newsData: '',
  };

  const [state, setState] = useState(initialState);
  // useEffect(() => {
  //   getData();
  // }, []);
 const  getQueryonSubmit = async (e) => {
        e.preventDefault();
        await setState({...state,
          searchQuery: e.target.elements[0].value,
        });
            await operations.getSummary().then((data) =>
            setState({...state,
              searchinfo: data.data.Countries.find(
                (country) =>
                  country.Country.toLowerCase() ===
                  state.searchQuery.toLowerCase()
              ),
            })
          );
    
          await operations
            .getFlag(state.searchQuery)
            .then((data) => setState({ ...state,flagQuery: data.data.hits }));
    
          await operations
            .getSearchnews(state.searchQuery)
            .then((data) => setState({ newsData: data.data.articles, }));
    
      };
      console.log('state', state)
  // const { searchinfo, flagQuery } = this.state;
