import axios from "axios";
import { withRouter } from "react-router-dom";

const KEY = "15302072-a81be31270c5e4995077a81d4";

export const operations= {
    async getSummary() {
      try {
        const data = await axios.get(`https://api.covid19api.com/summary`);

        return data;
      } catch (error) {
        console.log("GetSummaryError", error);
        throw new Error();
      }
    },
    async getFlag(country) {
      console.log('helloo')
      try {
        const data = await axios.get
        (`https://pixabay.com/api/?image_type=photo&orientation=horizontal&q=${country} flag&page=1&per_page=2&key=${KEY}`);
        console.log('data', data)
        return data;
      } catch (error) {
        console.log("GetSummaryError", error);
        throw new Error();
      }
    },
    
}
