import React, { Component } from "react";
import CovidList from "./CovidList/CovidList";
import {operations} from './operations/Operations'


class Covid19 extends Component {
  state = {
    globalInfo:[]
  };


  async componentDidMount() {
    await operations
      .getSummary()
      .then(data => this.setState({ globalInfo: data.data.Global }));
    
    // await operations.getFlag('spain')
  }


  render() {
    
    console.log('GLOBALINFO', this.state.globalInfo.TotalConfirmed)
    return(
        <>
         
       {/* <CovidList />
         */}
        </>
        
     ) ;
    
  }
}
export default Covid19;
