import React, { Component } from "react";
import CovidItem from "../CovidItem/CovidItem";
import { operations } from "../operations/Operations";

import css from './covidList.module.css'
class CovidList extends Component {
  state = {
    Countries:[],
    maxConfirmed: [],
    maxDead:[],
    totalRecovered: []
  };

  async componentDidMount() {
    if(this.props.Countries){

     await this.TotalStat()}
    // await operations.getFlag('spain')
  }

   TotalStat = async () => {
    const maxConfirmed = this.props.Countries.sort(
      (b, a) => a.TotalConfirmed - b.TotalConfirmed
    
    ).slice([0], [5]);
    const maxDead = this.props.Countries.sort(
        (b, a) => a.TotalDeaths - b.TotalDeaths
      
      ).slice([0], [5]);
    const totalRecovered = this.props.Countries.sort(
        (b, a) => a.TotalRecovered - b.TotalRecovered
      
      ).slice([0], [5]);
 this.setState({maxConfirmed:maxConfirmed, maxDead: maxDead,totalRecovered:totalRecovered})
    
  };


  render() {
    //   operations.getFlag()

    const { maxConfirmed,maxDead,totalRecovered } = this.state;

    console.log('This',this.props.Countries)
    return (
      <>
      {this.props.Countries&&
      <div className={css.container}>
          <div className={css.div_wrapper}>
        <CovidItem maxConfirmed={maxConfirmed} maxDead={maxDead} totalRecovered={totalRecovered} />
       
        </div>
        </div>
  }
      </>
    );
  }
}
export default CovidList;
