import React, { Component } from "react";
import { operations } from "../covid19/operations/Operations";
import css from "../homePage/homepage.module.css";
import SearchForm from "../covid19/Searchform/SearchForm";

class HomePage extends Component {
  state = {
    globalInfo: [],
    searchQuery: null,
    searchinfo: null,
    flagQuery: null,
  };

  async componentDidMount() {
    await operations
      .getSummary()
      .then((data) => this.setState({ globalInfo: data.data }));

    // await operations.getFlag('spain')
  }

   async componentDidUpdate(prevProps, prevState) {
    if (prevState.searchQuery !== this.state.searchQuery) {
    //   operations.getLiveInfo(this.state.searchQuery).then((data) =>
    //     this.setState({
    //       searchinfo: data,
    //     })
    //   ); await operations
    await  operations.getSummary()
      .then((data) => this.setState({ searchinfo: data.data.Countries.find(country => country.Country === this.state.searchQuery) }));



     await  operations
        .getFlag(this.state.searchQuery)
        .then((data) => this.setState({ flagQuery: data.data.hits }));
    }
 
  }
  
  getQueryonSubmit = async (e) => {
    e.preventDefault();
    await this.setState({
      searchQuery: e.target.elements[0].value,
    });
  };

  render() {
    
    const { searchinfo, flagQuery } = this.state;
    console.log('this.state', this.state)
    const { Global, Date } = this.state.globalInfo;
    // console.log('this.state.query', this.state.searchQuery)
    // console.log('this.state.searchinfo', this.state.searchinfo)
    console.log('this.state.searchinfo', this.state.searchinfo)

    console.log("FLAGQUERY", this.state.flagQuery);

    return (
      <>
        {this.state.globalInfo.Global && (
          <div className={css.homePageContainer}>
            <h1 className={css.covidTitle}> COVID 19 info</h1>
            <div className={css.totalCasesContainer}>
              <h2 className={css.totalCasesTitle}> Total cases in the world</h2>
              <p className={css.totConfirmed}>
                Total Confirmed:{Global.TotalConfirmed.toLocaleString()}
              </p>
              <p className={css.totConfirmed}>
                New Confirmed cases :{Global.NewConfirmed.toLocaleString()}
              </p>
              <p className={css.totConfirmed}>
                Total Deaths :{Global.TotalDeaths.toLocaleString()}
              </p>
              <p className={css.totConfirmed}>
                New Deaths :{Global.NewDeaths.toLocaleString()}
              </p>
              <p className={css.totConfirmed}>
                Total Recovered :{Global.TotalRecovered.toLocaleString()}
              </p>
              <p className={css.totConfirmed}>
                New Recovered :{Global.NewRecovered.toLocaleString()}
              </p>
              <p className={css.totConfirmed}>
                Last updated at :{Date.toLocaleString()}
              </p>
            </div>
          </div>
        )}
        <SearchForm getQueryonSubmit={this.getQueryonSubmit} />
        {searchinfo && flagQuery && (
          <div className={css.liveInfoContainer}>
            <div className={css.countryImage}>
              <img
                className={css.flag}
                alt="not available"
                src={flagQuery[0].webformatURL}
              />
            </div>
            <h2>Country:{searchinfo.Country}</h2>
            <h3> Three day statistics:</h3>
            <ul className={css.liveInfoList}>
              
                <li key={searchinfo.Date} className={css.oneDayInfo}>
                   <p  className={css.infoFirst}>Total cases:{searchinfo.TotalConfirmed}</p>
      <p className={css.infoFirst}>New cases:{searchinfo.NewConfirmed}</p>
      <p className={css.infoSecond}>Total deaths:{searchinfo.TotalDeaths}</p>
      <p className={css.infoSecond}>New deaths:{searchinfo.NewDeaths}</p>
      <p className={css.infoThird}>Total recovered:{searchinfo.TotalRecovered}</p>
      <p className={css.infoThird}>New recovered:{searchinfo.NewRecovered}</p>
                </li>
            </ul>
          </div>
        )}
      </>
    );
  }
}
export default HomePage;
