import React, { Component } from "react";
import { operations } from "../covid19/operations/Operations";
import css from "../homePage/homepage.module.css";
import SearchForm from '../covid19/Searchform/SearchForm'

class HomePage extends Component {
  state = {
    globalInfo: [],
    searchQuery:null,
    searchinfo:null
  };

  async componentDidMount() {
    await operations
      .getSummary()
      .then((data) => this.setState({ globalInfo: data.data }));

    // await operations.getFlag('spain')
  }
  

  componentDidUpdate(prevProps, prevState) {
     if (prevState.searchQuery !== this.state.searchQuery) {
        operations
       .getLiveInfo(this.state.searchQuery)
       .then(data =>
         this.setState({
           searchinfo: data,
            })
       )
    
       
     }

  
 }
  getQueryonSubmit =async e => {
    e.preventDefault();
  await  this.setState({
      searchQuery: e.target.elements[0].value,
      
    });
}

  render() {
      const x='2020-04-04T00:00:00Z'
      const {searchinfo}=this.state
    const {Global,Date}=this.state.globalInfo;
    console.log('this.state.query', this.state.searchQuery)
    console.log('this.state.searchinfo', this.state.searchinfo)


    return (
      <>
        {this.state.globalInfo.Global&&
        <div className={css.homePageContainer}>
          <h1 className={css.covidTitle}> COVID 19 info</h1>
          <div className={css.totalCasesContainer}>
              <h2 className={css.totalCasesTitle}> Total cases in the world</h2>
    <p className={css.totConfirmed}>Total Confirmed:{Global.TotalConfirmed.toLocaleString()}</p>
    <p className={css.totConfirmed}>New Confirmed cases :{Global.NewConfirmed.toLocaleString()}</p>
    <p className={css.totConfirmed}>Total Deaths :{Global.TotalDeaths.toLocaleString()}</p>
    <p className={css.totConfirmed}>New Deaths :{Global.NewDeaths.toLocaleString()}</p>
    <p className={css.totConfirmed}>Total Recovered :{Global.TotalRecovered.toLocaleString()}</p>
    <p className={css.totConfirmed}>New Recovered :{Global.NewRecovered.toLocaleString()}</p>
    <p className={css.totConfirmed}>Last updated at :{Date.toLocaleString()}</p>
      </div>
        </div>
      
  }
    <SearchForm getQueryonSubmit={this.getQueryonSubmit}/>
     {searchinfo&&
     <div className={css.liveInfoContainer}>
         <h2>Country:{searchinfo.data[0].Country}</h2>
         <ul className={css.liveInfoList}>
         {searchinfo.data.map(country=>(
                 <li key={country.Date} className={css.oneDayInfo}>
                     <p> {country.Country}</p>
                 </li>

         ))}

         </ul>
     </div>
     
     
     
     
     }
      </>
    );
  }
}
export default HomePage;
