import React,{useState} from "react";
import css from "./latestNews.module.css";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { v4 as uuidv4 } from "uuid";



const useStyles = makeStyles({
  root: {
    width: "100%",
  },
  media: {
    height: 220,
  },
});



const LatestNews = ({ newsData }) => {
  const initialState={
    newsData,
  }
  // const [newsData, setNewsState] = useState(initialState);
  const classes = useStyles();
if(newsData.map(elem=>(elem.title.length>100)))
{
  console.log('HIII')
}

  console.log('newsData', newsData[0].title.length)
  return (
    <>
      {newsData && <h2 className={css.title}>Latest news</h2>}

      <div className={css.newsContainer}>
        {newsData &&
          newsData.map((news) => (
            <div key={uuidv4()} className={css.cardDiv}>
            <Card  className={classes.root}>
              <CardActionArea>
                <CardMedia
                  className={classes.media}
                  image={news.urlToImage||'https://image.flaticon.com/icons/svg/2780/2780120.svg'}
                  title="Image Not Available"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="h2">
                    {news.title}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    {news.description}
                  </Typography>
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button variant="contained" color="primary">
                  <a className={css.link} href={news.url}>
                    Learn More
                  </a>
                </Button>
              </CardActions>
            </Card>
            </div>
          ))}
      </div>

      <div className={css.icons}>
        Icons made by{" "}
        <a href="https://www.flaticon.com/authors/freepik" title="Freepik">
          Freepik
        </a>{" "}
        from{" "}
        <a href="https://www.flaticon.com/" title="Flaticon">
          www.flaticon.com
        </a>
      </div>
    </>
  );
};

export default LatestNews;
